//set sessionId if needed
if (msg.payload.wfdSessionId === null){
    msg.payload.wfdSessionId = msg.payload.eventId;
}

//generate a tracking message
//must use msg for now
trackingMsg = msg;

//set parent and event ids
msg.payload.parentEventId = msg.payload.eventId;
msg.payload.eventId = null; //next service is expected to populate eventId

return [trackingMsg, msg];

// do flowOnEnd stuff
// need to give this more thought, maybe just track, do nothing for now
return msg;

//The following were set in the UI from a select box or something like that
//when this WFD was created


msg.payload.flowContext.locationId = "locationId-123";
msg.payload.flowContext.deviceId = "deviceId-123";
msg.payload.flowContext.WSG_Transaction = "GET_ITEM";

//The WSG services will use the Channel Transaction Parms
//for the transaction referenced above to formulate the proper request


return msg;

//1) save the originial message.  It will need updated and sent back
//   to the router
flow.set("payload", msg.payload);

//2) The channel transaction properties are cached in the WSG DB
//   Look up the channel transaction properties to get the 
//   username, password, baseURL, API end point etc...
//   format url according to the url pattern (also specified on the transaction properties)
//   
// Below is the end result of this activity:
msg.url ="https://57e4f4625569a2ab8d8daf2239dabc17:b3e784e1a7aa163e0a402e2925d0e554@aeg-store1.myshopify.com/admin/products.json";
msg.method = "GET";

return msg;

//save the web service response
wsResponse = msg.payload;

//restor the message from the session context
msg.payload = flow.get("payload");

//set the wsResponse as an INLINE attachment 
msg.payload.flowContext.wsgResponseFilename = "wsgresponse.json";
msg.payload.payloadAttachments.push({"location":"ATTACHED","fileName": "wsgresponse", "charEncoding": "UTF-8", "fileType":"json","inlineData":wsResponse});

//Generate uniue eventId and populate date/time for downstream tracking
msg.payload.eventid = "eventId-GUID";
msg.payload.eventDateTime = "20170205T23:25:32.000+0000";

//set even info
msg.payload.eventSource = "WSG";
msg.payload.eventType = "WS_RESPONSE";

// Ask the orchestrator to ROUTE
msg.payload.routeAction = "ROUTE";
return msg;
